import "./About.css";
import tweet_search from "../Images/dark-tweet.jpg";

const About = () => {
    return (
        <div className="about-container">
            <div className="about-text">
                <h1 className="about-header">
                    Welcome to the Twitter Search and Random Tweet Generator
                    App!
                </h1>
                <div className="about-body">
                    <div className="search-body">
                        <h2 className="search-header">Tweet Search Feature</h2>
                        <img src={tweet_search} alt="search-tweets"></img>
                        <p className="search-text"></p>
                    </div>
                    <div className="random-body">
                        <h2 className="random-header">
                            Random Tweet Generator
                        </h2>
                        <img src="" alt="random-tweets"></img>
                        <p className="random-text"></p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default About;
